private func eval_macroexpand(list: MalSequence, _ env: Environment) throws -> TCOVal {                                         // malstep(8,9,A)
    guard list.count >= 2 else {                                                                                                // malstep(8,9,A)
        try throw_error("Expected parameter to 'macroexpand'")                                                                  // malstep(8,9,A)
