private func RE(text: String, _ env: Environment) -> MalVal? {                                                                  // malstep(2,3,4,5,6,7,8,9,A)
    if !text.isEmpty {                                                                                                          // malstep(1,2,3,4,5,6,7,8,9,A)
        do {                                                                                                                    // malstep(1,2,3,4,5,6,7,8,9,A)
