    }                                                                                                                           // malstep(1,2,3,4,5,6,7,8,9,A)
    return nil                                                                                                                  // malstep(1,2,3,4,5,6,7,8,9,A)
}                                                                                                                               // malstep(1,2,3,4,5,6,7,8,9,A)
